	public function seemymodel_options_sanitize($input) {
		$sanitary_values = array();
		if ( isset( $input['seemymodel_user_email_0'] ) ) {
			$sanitary_values['seemymodel_user_email_0'] = sanitize_text_field( $input['seemymodel_user_email_0'] );
		}

		if ( isset( $input['seemymodel_user_password_1'] ) ) {
			$pass = sanitize_text_field( $input['seemymodel_user_password_1'] );
			$endpoint = 'https://api.seemymodel.com/auth/login';
 
			$body = [
				
				'email' => $sanitary_values['seemymodel_user_email_0'],
				'password' => $pass
			];
			
			$body = wp_json_encode( $body );
			
			$options = [
				'body'        => $body,
				'headers'     => [
					'Content-Type' => 'application/json',
				],
				'timeout'     => 60,
				'redirection' => 5,
				'blocking'    => true,
				'sslverify'   => true,
				'data_format' => 'body',
			];
			
			$response = wp_remote_post( $endpoint, $options );
			// Response body.
			$body = wp_remote_retrieve_body( $response );

			if ( is_wp_error( $response ) ) {
				$error_message = $response->get_error_message();
				add_settings_error('seemymodel_user_email_0','500', 'Unknown error');
			} else {
				$responceData = json_decode( $body, true );
				$status = wp_remote_retrieve_response_code( $response );
				if ($status == 201)
					$sanitary_values['seemymodel_user_password_1'] = $responceData['accessToken'];
				else if ($status == 401){
					add_settings_error('seemymodel_user_email_0','401', 'Wrong email or password');
				}else {
					add_settings_error('seemymodel_user_email_0','500', 'Unknown error');
				}

			}
			
		}

		return $sanitary_values;
	}

