	public function seemymodel_user_email_0_callback() {
		printf(
			'<input class="regular-text" type="text" name="seemymodel_options_option_name[seemymodel_user_email_0]" id="seemymodel_user_email_0" value="%s">',
			isset( $this->seemymodel_options_options['seemymodel_user_email_0'] ) ? esc_attr( $this->seemymodel_options_options['seemymodel_user_email_0']) : ''
		);
	}

	public function seemymodel_user_password_1_callback() {
		printf(
			'<input class="regular-text" type="password" name="seemymodel_options_option_name[seemymodel_user_password_1]" id="seemymodel_user_password_1" value="%s">',
			$this->logged_in() ? '*********' : ''
		);
	}

    public function logged_in() {
        
        $seemymodel_options_options = get_option( 'seemymodel_options_option_name' ); // Array of All Options
        if (!isset($seemymodel_options_options['seemymodel_user_password_1'])) return false;
        $admin_seemymodel_jwt_token = $seemymodel_options_options['seemymodel_user_password_1']; // SeeMyModel user password
			
        $endpoint = 'https://api.seemymodel.com/user-profile';
        $options = [
            'headers'     => [
                'Authorization' => 'Bearer ' . $admin_seemymodel_jwt_token,
            ],
            'timeout'     => 60,
            'redirection' => 5,
            'blocking'    => true,
            'sslverify'   => true,
        ];
        
        $response = wp_remote_get( $endpoint, $options );
		
        if ( is_wp_error( $response ) ) {
            return false;
        } else {
            $status = wp_remote_retrieve_response_code( $response );
            if ($status == 200) return true;
            return false;
        }
       
    }

}

